import React from 'react'
import {useState} from 'react';

var userName;

function Form() {
    // const [name, setName] = useState("");
    return (
        <div>
            <form>
                <label>Enter your name: </label>
                <input type="text" id='nameField' value={userName} onChange=
                { (e) => {
                        // setName(e.target.value);
                        userName = e.target.value;
                    }
                }/>
            </form><br/>
            {/* <h1>Your name is {name}, right?</h1> */}
            <input type="button" id="buttonName" value="Check name" onClick={butonName}></input>
        </div>
    )
}

function butonName(){
    var butonName = document.querySelector('#buttonName');
    butonName.addEventListener('click', () => {
        var text = document.createElement('h2');
        text.innerHTML = `Your name is ${userName}, right?`;
        document.body.append(text);
    });
}

export default Form;
