import React from 'react';
import './RegisterPage.css';

function inregistrare(){
    if(checkValues === true){
        alert('Inregistrarea s-a efectuat cu succes!');
    }
    else{
        alert('Nu s-a efectuat inregistrarea!');
    }
}

function checkValues(){
    var numeText = document.querySelector('#nume');
    var prenumeText = document.querySelector('#prenume');

    if(numeText.value.length == 0){
        alert('Nu ati introdus numele!');
        return false;
    }

    if(!prenumeText.value.length == 0){
        alert('Nu ati introdus prenumele!');
        return false;
    }

    return true;
}

function RegisterPage(){
    return(
        <div>
            <h1>Registration form</h1>
            <div className='formBox'>
                <form>
                    <label for="nume">Nume</label>
                    <input id="nume" type={"text"}/>

                    <label for="prenume">Prenume</label>
                    <input id="prenume" type={"text"}/>
                
                    <label for="type">Type</label>
                    <select id="type">
                        <option id="optionTester">TESTER</option>
                        <option id="optionDeveloper">DEVELOPER</option>
                    </select>
                    
                    <input id="registerButton" type="button" value="Register" onClick={() => {inregistrare()}}/>
                </form>
            </div>
        </div>
    );
}

export default RegisterPage;
